mapper.select:
SELECT * FROM video


        vid not in

            #{one}



ORDER BY RAND()
LIMIT #{num};

select * from `video`
where
vid = #{vid}

select * from `watch_info`
where uid = #{uid}
order by time desc
limit #{num};

select * from `video`
where uid = #{uid}
and pass != 3

SELECT maintag, COUNT(maintag) as num FROM `video`
GROUP BY maintag
ORDER BY COUNT(maintag) DESC;

select count(*) from `watch_info`
where uid = #{uid}
and DATE_SUB(CURDATE(),  INTERVAL 1 MONTH) <= date(time);

select count(*) from `dm`
where vid = #{vid}
and DATE_SUB(CURDATE(),  INTERVAL 1 MONTH) <= date(time);

select count(*) from `like_info`
where hisuid = #{uid}
and DATE_SUB(CURDATE(),  INTERVAL 1 MONTH) <= date(time);

select count(*) from `share_info`
where vid = #{vid}
and DATE_SUB(CURDATE(),  INTERVAL 1 MONTH) <= date(time);

select count(*) from `fav_info`
where vid = #{vid}
and DATE_SUB(CURDATE(),  INTERVAL 1 MONTH) <= date(time);

select count(*) from `icon_info`
where vid = #{vid}
and DATE_SUB(CURDATE(),  INTERVAL 1 MONTH) <= date(time);

